          <input type="text" name="atype" onChange={(e) => setCategory(e.target.value)}/>
        </div>
        <div>
          <label>Price:</label>
          <input type="text" name="price" onChange={(e) => setPrice(e.target.value)}/>
        </div>
        <div>
