          <input type="text" name="rating" onChange={(e) => setRating(e.target.value)}/>
        </div>
        <button type="submit">Submit</button>
      </form> */}

      <Router>
        <ProductList path="/products/"/>
        <Detail path="/detail"/>
        <Login path="/login"/>
        <NewProduct path="/new"/>
        <Register path="/register"/>
      </Router>
    </div>
  );
}

export default App;
